export default {
  name: 'AzCallToAction',
  props: {
    active: {
      type: Boolean,
      default: false
    },
    disabled: {
      type: Boolean,
      default: false
    },
    dark: {
      type: Boolean,
      default: false
    },
    hideBorder: {
      type: Boolean,
      default: false
    },
    cssClass: {
      type: String,
      default: ''
    }
  },
  computed: {
    color: function color() {
      if (this.active) {
        return 'secondary';
      } else if (this.dark) {
        return '';
      } else {
        return 'primary';
      }
    },
    isDark: function isDark() {
      return this.dark && !this.active;
    },
    isOutline: function isOutline() {
      return !this.active;
    },
    style: function style() {
      var styleObj = {
        'call-to-action': true,
        'hide-border': this.hideBorder,
        'btn-disabled-action': this.disabled
      };

      if (this.cssClass) {
        var classes = this.cssClass.split(' ');
        classes.forEach(function (clazz) {
          return styleObj[clazz] = true;
        });
      }

      return styleObj;
    }
  }
};
