import mutationTypes from '@/core/constants/mutationTypes';
import LabelPersonalizado from '@/views/components/label/LabelPersonalizado';
import ReconhecimentoForm from './ReconhecimentoForm';
import Paginacao from '@/views/components/tabela/Paginacao';
export default {
  name: 'ReconhecimentoListagem',
  components: {
    Paginacao: Paginacao,
    ReconhecimentoForm: ReconhecimentoForm,
    LabelPersonalizado: LabelPersonalizado
  },
  props: ['value', 'paginacao', 'paginas', 'totalItens', 'permitirEdicao'],
  data: function data() {
    return {
      paginacaoInterna: this.paginacao,
      permitirAcoes: this.permitirEdicao,
      nomeTela: 'RECONHECIMENTO'
    };
  },
  computed: {
    colunas: function colunas() {
      return this.criarColunas(5, [], ['nome', 'situacao', 'execucaoOrcamentaria', 'camposObrigatorios', 'acoes'], [true, true, true, false, false], ['left', 'left', 'left', 'left', 'left'], ['50px', '50px', '150px', '150px', '10px'], 'gray--text');
    }
  },
  watch: {
    paginacaoInterna: {
      handler: function handler(novoValor) {
        this.$emit('paginar', novoValor);
      },
      deep: true
    }
  },
  methods: {
    atualizarReconhecimento: function atualizarReconhecimento(reconhecimento) {
      this.$emit('atualizarReconhecimento', reconhecimento);
    },
    cancelarEdicao: function cancelarEdicao() {
      this.habilitarAcoes();
      this.$emit('cancelarEdicao');
    },
    desabilitaBotaoNovo: function desabilitaBotaoNovo() {
      this.$emit('desabilitaBotaoNovo');
    },
    habilitaBotaoNovo: function habilitaBotaoNovo() {
      this.$emit('habilitaBotaoNovo');
    },
    desativarAcoes: function desativarAcoes() {
      this.permitirAcoes = false;
    },
    habilitarAcoes: function habilitarAcoes() {
      this.permitirAcoes = true;
    },
    inserirNovoReconhecimento: function inserirNovoReconhecimento(novoReconhecimento) {
      this.$emit('inserirNovoReconhecimento', novoReconhecimento);
    },
    tratarPaginacao: function tratarPaginacao(pagina) {
      this.paginacaoInterna.page = pagina;
    },
    resetaPage: function resetaPage() {
      this.$store.commit(mutationTypes.CONFIGURACAO.RECONHECIMENTO.RESETA_PAGE_RECONHECIMENTO);
    }
  }
};
